var inputs = $('.form :input').not('button, [type="button"], [type="submit"]').on({input: cback});

function cback() {
    var bad = inputs.filter(function() {
        return !this.value.length;
    }).length,
        total = inputs.length;

    $(".top").css("width", 100 - (bad / total) * 100 + "%");
}
